<html lang="en">
<style>
    body {
        background: url(data:image/jpeg;base64,);
    }

    #no_background {
        background: white;
        position: fixed;
        left: 0px;
        top: 0px;
        width: 100%;
        height: 100%;
        z-index: 1;
    }

    #wrapper {
        position: relative;
        z-index: 2;
    }
</style>
<script>
    $("#test").click(function() {
        $("#no_background").fadeOut();
    });
</script>
<body>
<div id="no_background"></div>
<div id="wrapper">
    <!-- page content goes here -->
    <input type="button" id="test" value="Show background">
</div>
</body>
</html>